#!/usr/bin/env osascript -l JavaScript

function run([argv]) {
    ObjC.import('stdlib')
    const envDocuments = $.getenv("documents");
    const action = $.getenv("action");
    var { op, files, base: BASE } = JSON.parse(envDocuments);

    if (action === "choose op") {
        op = argv
    }

    const App = Application.currentApplication();
    App.includeStandardAdditions = true;

    const FinderPath = () =>
        decodeURI(
            Application('Finder')
                .insertionLocation()
                .url()
                .replace(/^file:\/\//, ''),
        );

    const ACTIONS = {
        pasteboard: (files) => {
            ObjC.import('AppKit');
            const pasteboard = $.NSPasteboard.generalPasteboard;
            pasteboard.clearContents;
            []
                .concat(files)
                .forEach((f) =>
                    pasteboard.writeObjects([
                        ObjC.unwrap($.NSURL.fileURLWithPath(`${BASE}/${f}`)),
                    ]),
                );
        },
        move: (files) =>
            files.forEach((file) =>
                App.doShellScript(`mv "${BASE}/${file}" "${FinderPath()}"`),
            ),
        copy: (files) =>
            files.forEach((file) =>
                App.doShellScript(`cp "${BASE}/${file}" "${FinderPath()}"`),
            ),
    };

    if (op != "pasteboard"){
        const frontmost_app_name = Application('System Events').applicationProcesses.where({ frontmost: true }).name()[0]
        if (!frontmost_app_name.startsWith('Finder')) {
            return "You need put Finder as your frontmost app"
        }
    }

    ACTIONS[op](files);
}

