#!/usr/bin/env osascript -l JavaScript

function run([argv]) {
  ObjC.import('stdlib')

  const op = $.getenv('op')
  const file = $.getenv('file')

  const App = Application.currentApplication();
  App.includeStandardAdditions = true;

  const FinderPath = () =>
    decodeURI(
      Application('Finder')
        .insertionLocation()
        .url()
        .replace(/^file:\/\//, ''),
    );

  const ACTIONS = {
    move: (file) => App.doShellScript(`mv "${file}" "${FinderPath()}"`),
    copy: (file) => App.doShellScript(`cp "${file}" "${FinderPath()}"`),
  };

  ACTIONS[op](file);
}
